import { useState, useRef, useEffect } from "react"
import { URL } from "../constants"
import "./ChatPage.css"

const ChatPage = () => {
    const [input, setInput] = useState("")
    const [messages, setMessages] = useState([])
    const [returnMessage, setReturnMessage] = useState("")
    const [talking, setTalking] = useState(false)
    const [loading, setLoading] = useState(false)
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);

    const handleSend = async () => {
        if (input.trim() !== '') {
          // Add AI's message
          setLoading(true);
          const fd = new FormData();
          fd
          const response = await fetch(`http://127.0.0.1:8000/api/talk`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: input
          })
          const reader = response.body.getReader();
          setLoading(false);
          let incomingText = ""
          while (true) {
            const { done, value } = await reader.read();
            if (done) break;
            const messages = new TextDecoder('utf-8').decode(value);
            incomingText += messages
            setReturnMessage(incomingText)
          }
        }
        setInput("")
        setTalking(false)
      }

    useEffect(() => {
        setTalking(!talking)
    }, [returnMessage])

    return(
        <div className="chat-page">
            <div className="chat-interface">
                <div className="chat-send-form">
                    <input className="chat-input"
                        type="text"
                        value={input}
                        placeholder="Ask away..."
                        onChange={e => setInput(e.target.value)}
                        onKeyPress={e => e.key === 'Enter' ? handleSend() : null}
                    />
                    <span className="send-button "></span>
                </div>
                <div className={`skull-head-${talking ? "close" : "open"}`}></div>
                <div className="return-message">
                    {returnMessage}
                </div>
            </div>
        </div>
    )
}
export default ChatPage;
