import { useState, useRef, useEffect } from "react"
import { URL } from "../constants"
import "./ChatPage.css"

const ChatPage = () => {
    const [input, setInput] = useState("")
    const [returnMessage, setReturnMessage] = useState("")
    const [talking, setTalking] = useState(false)
    const [loading, setLoading] = useState(false)

    function sleep(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    const changeTalking = () => {
        console.log(loading)
        while(!loading) {
            sleep(200)
            setTalking(!talking)
        }
        setTalking(true)
    }

    const handleSend = async () => {
        setReturnMessage("")
        setTalking(true)
        if (input.trim() !== '') {
          setLoading(true);
          // Add the user's message
          changeTalking()
          setInput('');
          // Add AI's message
          const response = await fetch(`${URL}/api/talk`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              Body: input
            }),
          })
          const reader = response.body.getReader();
          let incomingText = ""
          while (true) {
            const { done, value } = await reader.read();
            if (done) break;
            const messages = new TextDecoder('utf-8').decode(value);
            incomingText += messages
            setReturnMessage(incomingText)
          }
          setLoading(false);
        }
      }

    useEffect(() => {
        // changeTalking()
    }, [loading])

    return(
        <div className="chat-page">
            <div className="chat-interface">
                <div className="chat-send-form">
                    <input className="chat-input"
                        type="text"
                        value={input}
                        placeholder="Ask away..."
                        onChange={e => setInput(e.target.value)}
                        onKeyPress={e => e.key === 'Enter' ? handleSend() : null}
                    />
                    <span className="send-button "></span>
                </div>
                <div className={`skull-head-${talking ? "close" : "open"}`}></div>
                <div className="return-message">
                    {returnMessage}
                </div>
            </div>
        </div>
    )
}
export default ChatPage;
