import { useState, useEffect } from "react"
import { URL } from "../constants"
import "./ChatPage.css"

const ChatPage = () => {
    const [input, setInput] = useState("")
    const [returnMessage, setReturnMessage] = useState("")
    const [talking, setTalking] = useState(false)
    const [loading, setLoading] = useState(false)

    const changeTalking = () => {setTalking(!talking)}

    const handleSend = async () => {
        setReturnMessage("Hello")
        let timer = setInterval(()=>{setTalking(!talking)}, 50); 
        console.log(`THE TIMER: ${timer}`)
        if (input.trim() !== '') {
          setLoading(true);
          // Add the user's message
          setInput('');
          // Add AI's message
          const response = await fetch(`${URL}/api/talk`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              Body: input
            }),
          })
          const reader = response.body.getReader();
          let incomingText = ""
          while (true) {
            const { done, value } = await reader.read();
            if (done) break;
            const messages = new TextDecoder('utf-8').decode(value);
            incomingText += messages
            setReturnMessage(incomingText)
          }
          setLoading(false);
          clearInterval(timer);
          timer = null
        }
        setTalking(false);
        return
      }

    // useEffect(() => {
    //   // Starting the timer
    //   console.log(loading)
    //   let timer
    //   if(loading === true){
    //     console.log("STARTED")
    //     timer = setInterval(() => {
    //       setTalking(!talking);  // updating the state
    //     }, 150); 
    //   } else {
    //     console.log("ENDED")
    //     timer = () => clearInterval(timer);
    //   }
    // }, [loading])

    return(
        <div className="chat-page">
            <div className="chat-interface">
                <div className="chat-send-form">
                    <input className="chat-input"
                        type="text"
                        value={input}
                        placeholder="Ask away..."
                        onChange={e => setInput(e.target.value)}
                        onKeyPress={e => e.key === 'Enter' ? handleSend() : null}
                    />
                    <span className="send-button "></span>
                </div>
                <div className={`skull-head-${talking === false ? "close" : "open"}`}></div>
                <div className="return-message">
                    {returnMessage}
                </div>
            </div>
        </div>
    )
}
export default ChatPage;
