import { useState, useRef, useEffect } from "react"
import "./ChatPage.css"

const ChatPage = () => {
    const [input, setInput] = useState("")
    const [messages, setMessages] = useState([])
    const [loading, setLoading] = useState(false)
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);

    return(
        <div className="chat-page">
            <div className="chat-interface">
                <div className="chat-send-form">
                    <input className="chat-input"
                        type="text"
                        value={input}
                        placeholder="Ask away..."
                        onChange={e => setInput(e.target.value)}
                        onKeyPress={e => e.key === 'Enter' ? console.log(input) : null}
                    />
                    <span className="send-button "></span>
                </div>
                <></>
                <div className="chat-messages">
                    {messages.map((message, index) => (
                        <div key={index}>
                            {message.role === "assistant" &&
                                <div className="chat-message-ai">
                                <div className="chat-message-text-ai">{message.text}</div>
                                </div>
                            }
                            {message.role === "user" &&
                                <div className="chat-message-user">
                                <div className="chat-message-text-user">{message.text}</div>
                                </div>
                            }
                        </div>
                    ))}
                    {loading &&
                        <div className="loading-state">
                        <img className="tribe-logo-loading"/>
                            I'm thinking thinking...
                        </div>
                    }
                    <div ref={messagesEndRef} />
                </div>
            </div>
        </div>
    )
}
export default ChatPage;
